import React from "react";
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from "recharts";
import { ChartTooltip } from "../ui/Primitives";
import { COLORS } from "../../constants/tokens";
import { TrendingUp } from "lucide-react";

export default function VolumeChart({ data }) {
  return (
    <div className="panel">
      <div className="panel-header">
        <div className="panel-title">
          <TrendingUp size={12} color={COLORS.cyan} />
          REQUEST VOLUME â€” 7 DAYS
        </div>
      </div>
      <div style={{ padding: "12px 4px 8px" }}>
        <ResponsiveContainer width="100%" height={140}>
          <BarChart data={data} margin={{ top: 4, right: 16, left: -10, bottom: 0 }}>
            <CartesianGrid strokeDasharray="2 4" stroke="rgba(255,255,255,0.04)" vertical={false} />
            <XAxis dataKey="d" tick={{ fontFamily:"JetBrains Mono",fontSize:10,fill:COLORS.muted }} tickLine={false} axisLine={false} />
            <YAxis tick={{ fontFamily:"JetBrains Mono",fontSize:10,fill:COLORS.muted }} tickLine={false} axisLine={false} />
            <Tooltip content={<ChartTooltip />} />
            <Bar dataKey="req" name="Requests" fill={COLORS.cyan}  opacity={0.7} radius={[2,2,0,0]} />
            <Bar dataKey="err" name="Errors"   fill={COLORS.red}   opacity={0.7} radius={[2,2,0,0]} />
          </BarChart>
        </ResponsiveContainer>
      </div>
    </div>
  );
}
