import React from "react";
import MetricCard from "./MetricCard";
import { COLORS } from "../../constants/tokens";

function cpuSeverity(v) { return v > 85 ? "crit" : v > 70 ? "warn" : "ok"; }
function cpuBadge(v)     { return v > 85 ? "CRITICAL" : v > 70 ? "ELEVATED" : "NORMAL"; }
function cpuBadgeVar(v)  { return v > 85 ? "down" : v > 70 ? "warn" : "ok"; }
function errSeverity(v)  { return v > 2 ? "crit" : v > 0.5 ? "warn" : "ok"; }
function errBadge(v)     { return v < 0.1 ? "▼ OK" : v < 2 ? "▲ WARN" : "▲ CRIT"; }
function errBadgeVar(v)  { return v < 0.1 ? "ok" : v < 2 ? "warn" : "down"; }

export default function MetricsGrid({ metrics, spark, onCardClick }) {
  const barVar = (sev) => ({ ok:"cyan", warn:"warn", crit:"crit" }[sev] ?? "cyan");

  const cpu    = metrics.cpu;
  const mem    = metrics.mem;
  const err    = metrics.err;
  const cpuSev = cpuSeverity(cpu);
  const errSev = errSeverity(err);

  return (
    <div className="metric-grid slide-up">
      {/* CPU */}
      <MetricCard
        label="CPU LOAD"
        value={`${cpu.toFixed(1)}%`}
        badge={cpuBadge(cpu)}
        badgeVariant={cpuBadgeVar(cpu)}
        desc="4 cores"
        barPct={cpu}
        barVariant={barVar(cpuSev)}
        severity={cpuSev}
        valueColor={cpuSev === "crit" ? COLORS.red : cpuSev === "warn" ? COLORS.amber : COLORS.text}
        onClick={() => onCardClick("cpu")}
      />

      {/* MEMORY */}
      <MetricCard
        label="MEMORY"
        value={`${(mem * 0.08).toFixed(1)} GB`}
        badge={`${mem.toFixed(0)}% USED`}
        badgeVariant={mem > 85 ? "down" : mem > 70 ? "warn" : "ok"}
        desc="of 8 GB"
        barPct={mem}
        barVariant={barVar(mem > 85 ? "crit" : mem > 70 ? "warn" : "ok")}
        severity={mem > 85 ? "crit" : mem > 70 ? "warn" : "ok"}
        onClick={() => onCardClick("mem")}
      />

      {/* RPS */}
      <MetricCard
        label="REQUESTS / S"
        value={`${(metrics.rps / 1000).toFixed(1)}K`}
        badge="▲ LIVE"
        badgeVariant="up"
        desc="p99: 42ms"
        spark={spark}
        sparkColor={COLORS.cyan}
        onClick={() => onCardClick("rps")}
      />

      {/* ERROR RATE */}
      <MetricCard
        label="ERROR RATE"
        value={`${err.toFixed(3)}%`}
        badge={errBadge(err)}
        badgeVariant={errBadgeVar(err)}
        desc="last 5m"
        barPct={Math.min(100, err * 20)}
        barVariant={barVar(errSev)}
        severity={errSev}
        valueColor={errSev === "crit" ? COLORS.red : errSev === "warn" ? COLORS.amber : COLORS.green}
        onClick={() => onCardClick("err")}
      />
    </div>
  );
}
