import React, { useState } from "react";

const C = {
  cyan:"#00D4FF",green:"#3FB950",red:"#F85149",amber:"#D29922",
  void:"#0D1117",surface:"#161B22",border:"#21262D",
  text:"#E6EDF3",muted:"#7D8590",abyss:"#080B12",
};
const mono = { fontFamily:"'JetBrains Mono',monospace" };

function StatCard({ label, value, sub, color = C.cyan, onClick }) {
  const [h, setH] = useState(false);
  return (
    <div onMouseEnter={() => setH(true)} onMouseLeave={() => setH(false)} onClick={onClick}
      style={{ background: C.void, border:`1px solid ${h?"rgba(0,212,255,0.4)":C.border}`,
        padding:"14px 18px", cursor: onClick?"pointer":"default", transition:"all 120ms",
        transform: h&&onClick?"translateY(-2px)":"none",
        boxShadow: h&&onClick?"0 0 20px rgba(0,212,255,0.08)":"none",
        position:"relative", overflow:"hidden" }}>
      <div style={{ position:"absolute",top:0,left:0,right:0,height:1,
        background:`linear-gradient(90deg,${color} 0%,transparent 60%)`,opacity:h?0.8:0.35 }}/>
      <div style={{ ...mono,fontSize:10,fontWeight:600,letterSpacing:"0.12em",
        textTransform:"uppercase",color:C.muted,marginBottom:8 }}>{label}</div>
      <div style={{ ...mono,fontSize:28,fontWeight:700,color,letterSpacing:"-0.02em",lineHeight:1 }}>{value}</div>
      {sub && <div style={{ fontSize:11,color:C.muted,marginTop:6 }}>{sub}</div>}
    </div>
  );
}

function KitCard({ kit, onClick }) {
  const [h, setH] = useState(false);
  const repColor = kit.repetition_count >= 5 ? C.green : kit.repetition_count >= 2 ? C.cyan : C.amber;
  return (
    <div onMouseEnter={() => setH(true)} onMouseLeave={() => setH(false)} onClick={onClick}
      style={{ background:C.void, border:`1px solid ${h?"rgba(0,212,255,0.35)":C.border}`,
        padding:16, cursor:"pointer", transition:"all 120ms",
        transform:h?"translateY(-2px)":"none",
        boxShadow:h?"0 0 20px rgba(0,212,255,0.08)":"none", position:"relative" }}>
      <div style={{ position:"absolute",top:0,left:0,right:0,height:1,
        background:`linear-gradient(90deg,${repColor} 0%,transparent 60%)`,opacity:0.5 }}/>
      <div style={{ display:"flex",alignItems:"flex-start",justifyContent:"space-between",marginBottom:10 }}>
        <div style={{ ...mono,fontSize:12,fontWeight:700,color:C.cyan }}>{kit.dimensions}</div>
        <span style={{ ...mono,fontSize:9,fontWeight:600,padding:"2px 7px",textTransform:"uppercase",
          background:`${repColor}15`,color:repColor,border:`1px solid ${repColor}35` }}>
          Ã—{kit.repetition_count} REUSE
        </span>
      </div>
      <div style={{ display:"grid",gridTemplateColumns:"1fr 1fr",gap:10,marginBottom:12 }}>
        <KitStat label="KITS NEEDED" value={kit.required_qty} />
        <KitStat label="REPETITIONS" value={kit.repetition_count} color={repColor} />
      </div>
      <div style={{ ...mono,fontSize:10,color:C.muted,marginBottom:4,textTransform:"uppercase",letterSpacing:"0.08em" }}>
        USED IN ELEMENTS
      </div>
      <div style={{ display:"flex",flexWrap:"wrap",gap:4 }}>
        {kit.used_in_elements.slice(0,6).map(el => (
          <span key={el} style={{ ...mono,fontSize:9,padding:"2px 6px",
            background:"rgba(0,212,255,0.08)",border:"1px solid rgba(0,212,255,0.2)",color:C.cyan }}>
            {el}
          </span>
        ))}
        {kit.used_in_elements.length > 6 && (
          <span style={{ ...mono,fontSize:9,color:C.muted }}>+{kit.used_in_elements.length-6} more</span>
        )}
      </div>
      {/* Reuse bar */}
      <div style={{ marginTop:12,height:3,background:C.surface,overflow:"hidden" }}>
        <div style={{ height:"100%", width:`${Math.min(100,(kit.repetition_count/10)*100)}%`,
          background:`linear-gradient(90deg,${repColor},${repColor}55)`, transition:"width 1s ease" }}/>
      </div>
      <div style={{ ...mono,fontSize:9,color:C.muted,marginTop:3 }}>
        {((kit.repetition_count/10)*100).toFixed(0)}% of max lifespan (10 pours)
      </div>
    </div>
  );
}

function KitStat({ label, value, color = C.text }) {
  return (
    <div>
      <div style={{ ...mono,fontSize:9,color:C.muted,textTransform:"uppercase",letterSpacing:"0.08em",marginBottom:2 }}>{label}</div>
      <div style={{ ...mono,fontSize:18,fontWeight:700,color }}>{value}</div>
    </div>
  );
}

/* â”€â”€ BoQ Table â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
function BoQTable({ kits, summary }) {
  const [copied, setCopied] = useState(false);
  const rows = kits.map((k, i) => ({
    no: i+1,
    desc: `Formwork Kit ${k.dimensions}m`,
    unit: "Set",
    qty: k.required_qty,
    remark: `Ã—${k.repetition_count} reuse Â· ${k.used_in_elements.join(", ")}`,
  }));

  const copyCSV = () => {
    const header = "Item,Description,Unit,Qty,Remark\n";
    const body = rows.map(r => `${r.no},"${r.desc}",${r.unit},${r.qty},"${r.remark}"`).join("\n");
    navigator.clipboard.writeText(header + body);
    setCopied(true); setTimeout(() => setCopied(false), 2000);
  };

  return (
    <div style={{ background:C.void,border:`1px solid ${C.border}`,overflow:"hidden" }}>
      <div style={{ display:"flex",alignItems:"center",justifyContent:"space-between",
        padding:"10px 16px",borderBottom:`1px solid ${C.border}`,background:"rgba(0,212,255,0.02)" }}>
        <div style={{ ...mono,fontSize:10,fontWeight:600,letterSpacing:"0.12em",
          textTransform:"uppercase",color:C.text }}>OPTIMIZED BILL OF QUANTITIES</div>
        <button onClick={copyCSV} style={{ ...mono,fontSize:10,background:"none",
          border:`1px solid ${C.border}`,color:C.muted,padding:"4px 10px",cursor:"pointer" }}>
          {copied ? "âœ“ COPIED" : "COPY CSV"}
        </button>
      </div>
      <table style={{ width:"100%",borderCollapse:"collapse",fontSize:13 }}>
        <thead>
          <tr style={{ borderBottom:`1px solid ${C.border}` }}>
            {["#","DESCRIPTION","UNIT","QTY REQUIRED","REMARK"].map(h => (
              <th key={h} style={{ ...mono,fontSize:10,fontWeight:600,letterSpacing:"0.08em",
                color:C.muted,padding:"8px 16px",textAlign:"left" }}>{h}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {rows.map((r, i) => {
            const [hov, setHov] = useState(false);
            return (
              <tr key={r.no} onMouseEnter={()=>setHov(true)} onMouseLeave={()=>setHov(false)}
                style={{ background:hov?"rgba(0,212,255,0.03)":i%2===0?"transparent":"rgba(8,11,18,0.3)",
                  borderBottom:`1px solid rgba(33,38,45,0.6)`, transition:"background 80ms" }}>
                <td style={{ ...mono,fontSize:11,color:C.muted,padding:"0 16px",height:38 }}>{r.no}</td>
                <td style={{ fontSize:13,color:C.text,padding:"0 16px" }}>{r.desc}</td>
                <td style={{ ...mono,fontSize:12,color:C.muted,padding:"0 16px" }}>{r.unit}</td>
                <td style={{ ...mono,fontSize:14,fontWeight:700,color:C.cyan,padding:"0 16px" }}>{r.qty}</td>
                <td style={{ fontSize:12,color:C.muted,padding:"0 16px",maxWidth:280 }}>{r.remark}</td>
              </tr>
            );
          })}
          {/* Totals row */}
          <tr style={{ borderTop:`2px solid ${C.border}`,background:"rgba(0,212,255,0.04)" }}>
            <td colSpan={3} style={{ ...mono,fontSize:11,fontWeight:700,color:C.text,padding:"10px 16px" }}>
              TOTAL OPTIMIZED KITS
            </td>
            <td style={{ ...mono,fontSize:16,fontWeight:700,color:C.cyan,padding:"10px 16px" }}>
              {summary.optimized_kits_required}
            </td>
            <td style={{ ...mono,fontSize:11,color:C.green,padding:"10px 16px" }}>
              vs {summary.original_boq_items} original units
            </td>
          </tr>
        </tbody>
      </table>
    </div>
  );
}

/* â”€â”€ Main â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€â”€ */
export default function KittingPage({ result, onKitClick, onGoToInput, push }) {
  const [activeTab, setActiveTab] = useState("kits");

  if (!result) {
    return (
      <div style={{ display:"flex",flexDirection:"column",alignItems:"center",
        justifyContent:"center",height:400,gap:16 }}>
        <div style={{ fontSize:48 }}>ðŸ“‹</div>
        <div style={{ ...mono,fontSize:14,color:C.muted,textAlign:"center" }}>
          No optimization result yet.<br/>Run an optimization first.
        </div>
        <button onClick={onGoToInput}
          style={{ background:C.cyan,border:"none",color:C.abyss,...mono,
            fontSize:12,fontWeight:700,padding:"10px 28px",cursor:"pointer" }}>
          GO TO INPUT â†’
        </button>
      </div>
    );
  }

  const S = result;
  const savingColor = S.estimated_cost_savings_percent >= 30 ? C.green
    : S.estimated_cost_savings_percent >= 15 ? C.cyan : C.amber;

  return (
    <div style={{ display:"flex",flexDirection:"column",gap:16 }}>

      {/* â”€â”€ Summary KPI cards â”€â”€ */}
      <div style={{ display:"grid",gridTemplateColumns:"repeat(4,1fr)",gap:12 }}>
        <StatCard label="ORIGINAL UNITS"   value={S.original_boq_items}    sub="Raw BoQ demand"       color={C.muted} />
        <StatCard label="OPTIMIZED KITS"   value={S.optimized_kits_required} sub="After ML optimization" color={C.cyan} />
        <StatCard label="REPETITION FACTOR" value={`Ã—${S.total_repetition_factor}`} sub="Avg reuse per kit" color={C.green} />
        <StatCard label="COST SAVINGS EST." value={`${S.estimated_cost_savings_percent}%`}
          sub={`${S.original_boq_items - S.optimized_kits_required} fewer units needed`} color={savingColor} />
      </div>

      {/* â”€â”€ Tabs â”€â”€ */}
      <div style={{ display:"flex",borderBottom:`1px solid ${C.border}`,gap:0 }}>
        {[["kits","KIT DETAILS"],["boq","BILL OF QUANTITIES"],["json","RAW RESPONSE"]].map(([id,label]) => (
          <div key={id} onClick={() => setActiveTab(id)}
            style={{ ...mono,fontSize:11,fontWeight:600,letterSpacing:"0.08em",
              padding:"10px 20px",cursor:"pointer",
              color:activeTab===id?C.cyan:C.muted,
              borderBottom:`2px solid ${activeTab===id?C.cyan:"transparent"}`,
              background:activeTab===id?"rgba(0,212,255,0.04)":"none",
              transition:"all 100ms" }}>
            {label}
          </div>
        ))}
      </div>

      {/* â”€â”€ Kit Details â”€â”€ */}
      {activeTab === "kits" && (
        <div style={{ display:"grid",gridTemplateColumns:"repeat(auto-fill,minmax(280px,1fr))",gap:12 }}>
          {S.kit_details.map((kit, i) => (
            <KitCard key={i} kit={kit} onClick={() => onKitClick?.(kit)} />
          ))}
        </div>
      )}

      {/* â”€â”€ BoQ â”€â”€ */}
      {activeTab === "boq" && <BoQTable kits={S.kit_details} summary={S} />}

      {/* â”€â”€ Raw JSON â”€â”€ */}
      {activeTab === "json" && (
        <div style={{ background:C.abyss,border:`1px solid ${C.border}` }}>
          <div style={{ padding:"8px 16px",borderBottom:`1px solid ${C.border}`,
            display:"flex",alignItems:"center",justifyContent:"space-between" }}>
            <span style={{ ...mono,fontSize:10,color:C.muted,textTransform:"uppercase" }}>
              GO API RESPONSE
            </span>
            <button onClick={() => { navigator.clipboard.writeText(JSON.stringify(S,null,2));
              push({title:"Copied",msg:"JSON copied to clipboard"},"ok"); }}
              style={{ ...mono,fontSize:10,background:"none",border:`1px solid ${C.border}`,
                color:C.muted,padding:"3px 8px",cursor:"pointer" }}>
              COPY
            </button>
          </div>
          <pre style={{ ...mono,fontSize:11,color:C.green,padding:16,
            overflowX:"auto",maxHeight:420,lineHeight:1.6,margin:0 }}>
            {JSON.stringify(S, null, 2)}
          </pre>
        </div>
      )}
    </div>
  );
}
