import React from "react";
import { RefreshCw, Bell, Settings, Terminal } from "lucide-react";
import { Tooltip } from "../ui/Primitives";
import { TOP_NAV } from "../../constants/config";
import { useApp, ACTIONS } from "../../context/AppContext";

export default function Topbar({ onRefresh, onOpenCmd, sidebarCollapsed }) {
  const { state, dispatch } = useApp();
  return (
    <header className="topbar">
      <div className="topbar-logo" style={{ width: sidebarCollapsed ? 56 : 240 }}>
        <div className="logo-diamond" />
        {!sidebarCollapsed && <div className="logo-text">OBS<span>IDIAN</span></div>}
      </div>
      <nav className="topnav">
        {TOP_NAV.map((n) => (
          <div key={n} className={`topnav-item ${state.activeTopNav === n ? "active" : ""}`}
            onClick={() => dispatch({ type: ACTIONS.SET_ACTIVE_TOPNAV, payload: n })}>
            {n.toUpperCase()}
          </div>
        ))}
      </nav>
      <div className="topbar-right">
        <Tooltip tip="⌘K — Command palette">
          <input className="obs-input" style={{ width: 200 }} placeholder="⌘K  Search commands…" onFocus={onOpenCmd} readOnly />
        </Tooltip>
        <Tooltip tip="Refresh data"><button className="icon-btn" onClick={onRefresh}><RefreshCw size={14} /></button></Tooltip>
        <Tooltip tip="Notifications"><button className="icon-btn"><Bell size={14} /></button></Tooltip>
        <Tooltip tip="Settings"><button className="icon-btn" onClick={() => dispatch({ type: ACTIONS.SET_ACTIVE_NAV, payload: "settings" })}><Settings size={14} /></button></Tooltip>
        <div className="avatar">SR</div>
      </div>
    </header>
  );
}
