import React, { useState } from "react";
import { AlertTriangle, CheckCircle, XCircle } from "lucide-react";
import { COLORS } from "../../constants/tokens";
import { INITIAL_ALERTS, ALERT_FILTERS } from "../../constants/config";

function AlertIcon({ status }) {
  if (status === "ok")   return <CheckCircle  size={14} color={COLORS.green} />;
  if (status === "warn") return <AlertTriangle size={14} color={COLORS.amber} />;
  return <XCircle size={14} color={COLORS.red} />;
}

export default function AlertsPanel({ onAlertClick, onAcknowledge }) {
  const [filterState, setFilterState] = useState("all");
  const [alerts, setAlerts] = useState(INITIAL_ALERTS);

  const filtered = filterState === "all"
    ? alerts
    : alerts.filter((a) => a.status === filterState);

  const acknowledgeAll = () => {
    setAlerts((prev) => prev.map((a) => ({ ...a, status: "ok" })));
    if (onAcknowledge) onAcknowledge();
  };

  return (
    <div className="panel" style={{ display:"flex", flexDirection:"column" }}>
      <div className="panel-header">
        <div className="panel-title">
          <AlertTriangle size={12} color={COLORS.cyan} />
          ACTIVE ALERTS
          <span style={{ color: COLORS.red, fontFamily:"JetBrains Mono,monospace", fontSize:10 }}>
            {alerts.filter((a) => a.status === "err").length} CRIT
          </span>
        </div>
        <div style={{ display:"flex", gap:2 }}>
          {ALERT_FILTERS.map((f) => (
            <div key={f} className={`tab-item ${filterState === f ? "active" : ""}`}
              style={{ padding:"0 7px", fontSize:9, height:28, display:"flex", alignItems:"center" }}
              onClick={() => setFilterState(f)}>
              {f.toUpperCase()}
            </div>
          ))}
        </div>
      </div>

      <div style={{ overflow:"auto", maxHeight:200 }}>
        {filtered.map((a) => (
          <div key={a.id} className="alert-item" onClick={() => onAlertClick(a)}>
            <AlertIcon status={a.status} />
            <div style={{ flex:1 }}>
              <div className="alert-title">{a.title}</div>
              <div className="alert-sub">{a.sub}</div>
            </div>
            <div className="alert-time">{a.time}</div>
          </div>
        ))}
      </div>

      <div className="log-footer" style={{ padding:"8px 16px" }}>
        <button className="btn-ghost" style={{ width:"100%", justifyContent:"center" }} onClick={acknowledgeAll}>
          ACKNOWLEDGE ALL
        </button>
      </div>
    </div>
  );
}
