import React from "react";
import { Activity, FileText, AlertTriangle, Radio, Key, Settings, ChevronLeft, ChevronRight, LayoutDashboard, ClipboardList, Layers, Package, History } from "lucide-react";
import { StatusDot } from "../ui/Primitives";
import { NAV_ITEMS } from "../../constants/config";
import { SIDEBAR_WIDTH, SIDEBAR_COLLAPSED } from "../../constants/tokens";
import { useApp, ACTIONS } from "../../context/AppContext";

const ICONS = { LayoutDashboard, Activity, FileText, AlertTriangle, Radio, Key, Settings, ClipboardList, Layers, Package, History };

export default function Sidebar({ metrics, onToggle }) {
  const { state, dispatch } = useApp();
  const collapsed = state.sidebarCollapsed;
  const width = collapsed ? SIDEBAR_COLLAPSED : SIDEBAR_WIDTH;
  return (
    <aside className="sidebar" style={{ width }}>
      <div className="sidebar-body">
        {!collapsed && <div className="sidebar-section">Navigation</div>}
        {NAV_ITEMS.map((item) => {
          const Ic = ICONS[item.icon] || Activity;
          return (
            <div key={item.id} className={`nav-item ${state.activeNav === item.id ? "active" : ""}`}
              onClick={() => dispatch({ type: ACTIONS.SET_ACTIVE_NAV, payload: item.id })} title={collapsed ? item.label : undefined}>
              <Ic size={16} style={{ flexShrink: 0 }} />
              {!collapsed && <span className="nav-label">{item.label}</span>}
            </div>
          );
        })}
        <div className="sidebar-divider" />
        <div className={`nav-item ${state.activeNav === "settings" ? "active" : ""}`}
          onClick={() => dispatch({ type: ACTIONS.SET_ACTIVE_NAV, payload: "settings" })} title={collapsed ? "Settings" : undefined}>
          <Settings size={16} style={{ flexShrink: 0 }} />
          {!collapsed && <span className="nav-label">Settings</span>}
        </div>
      </div>
      <div className="sidebar-footer">
        {!collapsed ? (
          <>
            <div className="status-row"><StatusDot color="green" /><span>ONLINE Â· {metrics.agents} agents</span></div>
            <div className="status-row" style={{ marginBottom: 10 }}><StatusDot color={metrics.cpu < 80 ? "green" : "amber"} /><span>UPTIME {metrics.uptime.toFixed(2)}%</span></div>
            <button className="btn-ghost" style={{ width: "100%", justifyContent: "center" }} onClick={onToggle}><ChevronLeft size={12} />COLLAPSE</button>
          </>
        ) : (
          <button className="icon-btn" style={{ margin: "0 auto", display: "flex" }} onClick={onToggle}><ChevronRight size={14} /></button>
        )}
      </div>
    </aside>
  );
}
