import React from "react";
import { X, CheckCircle } from "lucide-react";
import { AreaChart, Area, ResponsiveContainer } from "recharts";
import { DrawerField } from "../ui/Primitives";
import { COLORS } from "../../constants/tokens";
import { useApp } from "../../context/AppContext";

export default function Drawer({ data, chartData, onAction }) {
  const { state, closeDrawer } = useApp();
  const open = state.drawer.open;

  const title = {
    cpu:      "CPU DETAIL",
    mem:      "MEMORY DETAIL",
    rps:      "REQUEST RATE",
    err:      "ERROR RATE",
    log:      "LOG ENTRY",
    alert:    "ALERT DETAIL",
    service:  `SERVICE: ${data?.service?.service?.toUpperCase() ?? ""}`,
    services: "ALL SERVICES",
  }[data?.type] ?? "DETAIL";

  return (
    <>
      <div className={`drawer-overlay ${open ? "open" : ""}`} onClick={closeDrawer} />
      <div className={`drawer ${open ? "open" : ""}`}>
        <div className="drawer-header">
          <div className="drawer-title">{title}</div>
          <button className="icon-btn" onClick={closeDrawer}><X size={14} /></button>
        </div>
        <div className="drawer-body">
          {/* Metric drawers */}
          {(data?.type === "cpu" || data?.type === "mem" || data?.type === "rps" || data?.type === "err") && (
            <div className="fade-in">
              <div style={{ fontFamily:"JetBrains Mono,monospace", fontSize:44, fontWeight:700, color:COLORS.cyan, marginBottom:16 }}>
                {data?.value?.toFixed(data.type === "err" ? 3 : 1)}{data.type !== "rps" ? "%" : ""}
              </div>
              <div style={{ marginBottom:20 }}>
                <ResponsiveContainer width="100%" height={110}>
                  <AreaChart data={chartData}>
                    <defs>
                      <linearGradient id="dg2" x1="0" y1="0" x2="0" y2="1">
                        <stop offset="5%"  stopColor={COLORS.cyan} stopOpacity={0.2}/>
                        <stop offset="95%" stopColor={COLORS.cyan} stopOpacity={0}/>
                      </linearGradient>
                    </defs>
                    <Area type="monotone" dataKey="v" stroke={COLORS.cyan} strokeWidth={1.5} fill="url(#dg2)" dot={false} />
                  </AreaChart>
                </ResponsiveContainer>
              </div>
              <DrawerField label="Current" value={`${data?.value?.toFixed(2)}${data.type !== "rps" ? "%" : " req/s"}`} />
              <DrawerField label="Threshold" value={data.type === "cpu" ? "85%" : data.type === "mem" ? "85%" : "N/A"} />
              <div style={{ display:"flex", gap:8, marginTop:8 }}>
                <button className="btn-primary" onClick={() => onAction("alert-set", data.type)}>SET ALERT</button>
                <button className="btn-ghost">EXPORT CSV</button>
              </div>
            </div>
          )}

          {/* Log drawer */}
          {data?.type === "log" && (
            <div className="fade-in">
              <DrawerField label="Timestamp" value={data.log?.time} />
              <DrawerField label="Level"     value={data.log?.level} />
              <DrawerField label="Source"    value={data.log?.src} />
              <DrawerField label="Message"   value={data.log?.msg} />
              <DrawerField label="Trace ID"  value={`trace-${Math.random().toString(36).slice(2,10)}`} />
              <button className="btn-ghost" style={{ width:"100%", justifyContent:"center", marginTop:8 }} onClick={() => onAction("view-trace", data.log)}>
                VIEW FULL TRACE
              </button>
            </div>
          )}

          {/* Alert drawer */}
          {data?.type === "alert" && (
            <div className="fade-in">
              <DrawerField label="Alert"    value={data.alert?.title} />
              <DrawerField label="Service"  value={data.alert?.sub} />
              <DrawerField label="Severity" value={data.alert?.status?.toUpperCase()} />
              <DrawerField label="Fired"    value={data.alert?.time} />
              <div style={{ display:"flex", gap:8, marginTop:16 }}>
                <button className="btn-primary" onClick={() => { onAction("ack", data.alert); closeDrawer(); }}>
                  <CheckCircle size={13} />ACKNOWLEDGE
                </button>
                <button className="btn-danger" onClick={() => onAction("escalate", data.alert)}>ESCALATE</button>
              </div>
            </div>
          )}

          {/* Service drawer */}
          {data?.type === "service" && (
            <div className="fade-in">
              {Object.entries(data.service ?? {}).map(([k, v]) => (
                <DrawerField key={k} label={k.toUpperCase()} value={String(v)} />
              ))}
            </div>
          )}
        </div>
      </div>
    </>
  );
}
