import React, { useCallback, useState } from "react";
import "./index.css";
import { AppProvider, useApp, ACTIONS } from "./context/AppContext";
import { useNotifications }   from "./hooks/useNotifications";
import { useLiveMetrics }     from "./hooks/useLiveMetrics";
import { useKeyboard }        from "./hooks/useKeyboard";
import Topbar                 from "./components/layout/Topbar";
import Sidebar                from "./components/layout/Sidebar";
import Drawer                 from "./components/ui/Drawer";
import CommandPalette         from "./components/ui/CommandPalette";
import NotificationStack      from "./components/ui/NotificationStack";

// ── Pages ──────────────────────────────────────────────────────
import OverviewPage   from "./pages/OverviewPage";
import MetricsPage    from "./pages/MetricsPage";
import LogsPage       from "./pages/LogsPage";
import InputPage      from "./pages/InputPage";
import KittingPage    from "./pages/KittingPage";
import InventoryPage  from "./pages/InventoryPage";
import AlertsPage     from "./pages/AlertsPage";
import HistoryPage    from "./pages/HistoryPage";
import SettingsPage   from "./pages/SettingsPage";

function InnerApp() {
  const { state, dispatch, openDrawer, openCmd, setLoading, toggleSidebar } = useApp();
  const { notifications, push, dismiss } = useNotifications();

  // ── Shared optimization result (Input → Kitting) ─────────────
  const [optimizationResult, setOptimizationResult] = useState(null);

  // ── Enterprise Feature: Active Megaproject Context ───────────
  const [activeProject, setActiveProject] = useState("MUMBAI COASTAL ROAD - PKG 4");

  const onNewLog = useCallback((log) => {
    if (log.level === "ERROR")
      push({ title: "Site Alert", msg: log.msg }, "err");
  }, [push]);

  const {
    metrics, cpuChart, memChart, rpsChart,
    barData, sparkCpu, logs, refresh,
  } = useLiveMetrics(onNewLog);

  useKeyboard({
    "k+any": openCmd,
    "b+any": toggleSidebar,
    "r+any": handleRefresh,
    "1+any": () => dispatch({ type: ACTIONS.SET_ACTIVE_NAV, payload: "overview"  }),
    "2+any": () => dispatch({ type: ACTIONS.SET_ACTIVE_NAV, payload: "input"     }),
    "3+any": () => dispatch({ type: ACTIONS.SET_ACTIVE_NAV, payload: "kitting"   }),
    "4+any": () => dispatch({ type: ACTIONS.SET_ACTIVE_NAV, payload: "inventory" }),
  });

  function handleRefresh() {
    setLoading(true);
    refresh();
    setTimeout(() => {
      setLoading(false);
      push({ title: "Data Synced", msg: "L&T ERP Data Refreshed" }, "ok");
    }, 800);
  }

  // ── Drawer openers ────────────────────────────────────────────
  const openCardDrawer      = (type)    => openDrawer({ type,        value:   metrics[type] });
  const openLogDrawer       = (log)     => openDrawer({ type: "log",       log              });
  const openAlertDrawer     = (alert)   => openDrawer({ type: "alert",     alert            });
  const openServiceDrawer   = (service) => openDrawer({ type: "service",   service          });
  const openKitDrawer       = (kit)     => openDrawer({ type: "kit",       kit              });
  const openInventoryDrawer = (item)    => openDrawer({ type: "inventory", item             });
  const openHistoryDrawer   = (run)     => openDrawer({ type: "history",   run              });

  const handleDrawerAction = (action, payload) => {
    if (action === "alert-set") push({ title: "Rule Active",  msg: `Threshold set for ${(payload ?? "").toUpperCase()}` }, "ok");
    if (action === "ack")       push({ title: "Acknowledged",   msg: payload?.title ?? "Alert resolved by engineering" }, "ok");
    if (action === "escalate")  push({ title: "Escalated",      msg: "Sent to Site Manager via SMS" }, "warn");
    if (action === "view-trace")push({ title: "BIM Trace",      msg: "Opening 3D Digital Twin model…" }, "info");
    if (action === "rerun")     push({ title: "Re-running",     msg: `Loading simulation ${payload?.id}` }, "info");
  };

  // ── Called by InputPage when Go Backend completes ──────────
  const handleOptimizationComplete = useCallback((result) => {
    setOptimizationResult(result);
    dispatch({ type: ACTIONS.SET_ACTIVE_NAV, payload: "kitting" });
    push({
      title: "Optimization Complete",
      msg:  `${result.estimated_cost_savings_percent}% cost reduction · Engine Time: ${result.execution_time_ms}`,
    }, "ok");
  }, [dispatch, push]);

  // ── Page router ───────────────────────────────────────────────
  const renderPage = () => {
    switch (state.activeNav) {
      case "metrics":
        return <MetricsPage cpuData={cpuChart} memData={memChart} rpsData={rpsChart} metrics={metrics} />;
      case "logs":
        return <LogsPage logs={logs} onLogClick={openLogDrawer} onServiceClick={openServiceDrawer} push={push} />;
      case "input":
        return <InputPage push={push} setLoading={setLoading} onComplete={handleOptimizationComplete} />;
      case "kitting":
        return <KittingPage result={optimizationResult} onKitClick={openKitDrawer} onGoToInput={() => dispatch({ type: ACTIONS.SET_ACTIVE_NAV, payload: "input" })} push={push} />;
      case "inventory":
        return <InventoryPage onItemClick={openInventoryDrawer} push={push} />;
      case "alerts":
        return <AlertsPage onAlertClick={openAlertDrawer} push={push} />;
      case "history":
        return <HistoryPage onRunClick={openHistoryDrawer} onRerun={(run) => { push({ title: "Loading Run", msg: run.id }, "info"); openHistoryDrawer(run); }} push={push} />;
      case "settings":
        return <SettingsPage push={push} />;
      default:
        return (
          <OverviewPage
            metrics={metrics} spark={sparkCpu} cpuData={cpuChart} memData={memChart}
            rpsData={rpsChart} barData={barData} logs={logs} optimizationResult={optimizationResult}
            onCardClick={openCardDrawer} onLogClick={openLogDrawer} onAlertClick={openAlertDrawer}
            onServiceClick={openServiceDrawer} onGoToInput={() => dispatch({ type: ACTIONS.SET_ACTIVE_NAV, payload: "input" })}
            onRefresh={handleRefresh} push={push}
          />
        );
    }
  };

  return (
    <div className="obs-root">
      {state.loading && <div className="progress-bar" />}

      <Topbar
        onRefresh={handleRefresh}
        onOpenCmd={openCmd}
        sidebarCollapsed={state.sidebarCollapsed}
      />

      <div className="layout">
        <Sidebar metrics={metrics} onToggle={toggleSidebar} />

        <main className="main">
          {/* Enhanced Breadcrumb with Megaproject Selector */}
          <div className="breadcrumb" style={{ display: 'flex', alignItems: 'center' }}>
            <span style={{ fontWeight: 700, color: '#FFB81C' }}>L&T KIT-OPTIMA</span>
            <span className="bc-sep">›</span>

            <select 
              value={activeProject}
              onChange={(e) => setActiveProject(e.target.value)}
              style={{
                background: 'transparent', border: 'none', color: '#a1a1aa',
                fontFamily: "'JetBrains Mono',monospace", fontSize: 12, cursor: 'pointer',
                outline: 'none'
              }}
            >
              <option>MUMBAI COASTAL ROAD - PKG 4</option>
              <option>MAHSR BULLET TRAIN - C6</option>
              <option>NAVI MUMBAI INT. AIRPORT</option>
            </select>

            <span className="bc-sep">›</span>
            <span className="bc-current" style={{ color: '#fff' }}>{state.activeNav.toUpperCase()}</span>

            <span className="bc-sep">›</span>
            <span style={{ color: "#4a5568", fontFamily: "'JetBrains Mono',monospace", fontSize: 11 }}>
              {new Date().toLocaleTimeString("en-GB")}
            </span>

            {/* Enhanced ESG Success Badge */}
            {optimizationResult && (
              <span style={{
                marginLeft: "auto", fontFamily: "'JetBrains Mono',monospace", fontSize: 11,
                background: "rgba(63,185,80,0.1)", border: "1px solid rgba(63,185,80,0.4)",
                color: "#3FB950", padding: "4px 12px", borderRadius: "4px",
                letterSpacing: "0.05em", display: 'flex', alignItems: 'center', gap: '8px',
                boxShadow: "0 0 10px rgba(63,185,80,0.2)"
              }}>
                <span style={{ width: 6, height: 6, background: '#3FB950', borderRadius: '50%', display: 'inline-block' }}></span>
                {optimizationResult.estimated_cost_savings_percent}% MAT. SAVED | {Math.round(optimizationResult.estimated_cost_savings_percent * 1.8)}t CO₂ REDUCED
              </span>
            )}
          </div>

          <div className="content">{renderPage()}</div>
        </main>
      </div>

      <Drawer data={state.drawer.data} chartData={cpuChart} onAction={handleDrawerAction} />
      <CommandPalette onAction={(a) => a === "refresh" && handleRefresh()} />
      <NotificationStack notifications={notifications} onDismiss={dismiss} />
    </div>
  );
}

export default function App() {
  return <AppProvider><InnerApp /></AppProvider>;
}
